import numpy as np

def eva(tp, tn, fp, fn):
    accu = (tp + tn) / (tp + tn + fp + fn)
    recall = tp / (tp + fn)
    f1 = 2*tp / (2*tp + fp + fn)
    if (tp+fp) != 0:
        prec = tp / (tp + fp)
    else:
        prec = 0.0
    return accu, recall, f1, prec

def get_accu(scores, labels, threshold):
    tp = 0.0
    fp = 0.0
    tn = 0.0
    fn = 0.0
    for score, label in zip(scores, labels):
        if label == 1 and score >= threshold:
            tp += 1
        elif label == 1 and score < threshold:
            fn += 1
        elif label == 0 and score >= threshold:
            fp += 1
        elif label == 0 and score < threshold:
            tn += 1
    return tp, fp, tn, fn

def cosine(vec1, vec2):
    vector1 = np.array(vec1)
    vector2 = np.array(vec2)
    return np.dot(vector1,vector2)/(np.linalg.norm(vector1)*(np.linalg.norm(vector2)))


